import '../css/App.css';

class TransitionComp extends Component{
    state={
        show:false
    }

    render(){
        return(
            <>
                {this.state.show?<div style={{
                    background:'red',
                    height:'100px'
                }}></div>:null}



                <div>
                Transition
                </div>

            </>

        )
    }
}


export default TransitionComp;
